#!/system/bin/sh

CUST_ROOT=`getprop ro.lge.capp_cupss.rootdir /cust`
CUPSS_BOOTANIMATION_FILE=$CUST_ROOT/poweron/bootanimation.zip
CUPSS_BOOTANIMATION_SOUND_FILE=$CUST_ROOT/poweron/PowerOn.ogg
CUPSS_SHUTDOWNANIMATION_FILE=$CUST_ROOT/poweroff/shutdownanimation.zip
CUPSS_SHUTDOWNANIMATION_SOUND_FILE=$CUST_ROOT/poweroff/PowerOff.ogg

USER_BOOTANIMATION_FILE=/data/local/bootanimation.zip
USER_BOOTANIMATION_SOUND_FILE=/data/local/PowerOn.ogg
USER_SHUTDOWNANIMATION_FILE=/data/local/shutdownanimation.zip
USER_SHUTDOWNANIMATION_SOUND_FILE=/data/local/PowerOff.ogg

SYSTEM_ROOT=/system/meida
SYSTEM_BOOTANIMATION_FILE=$SYSTEM_ROOT/bootanimation.zip
SYSTEM_BOOTANIMATION_SOUND_FILE=$SYSTEM_ROOT/audio/ui/PowerOn.ogg
SYSTEM_SHUTDOWNANIMATION_FILE=$SYSTEM_ROOT/shutdownanimation.zip
SYSTEM_SHUTDOWNANIMATION_SOUND_FILE=$SYSTEM_ROOT/audio/ui/PowerOff.ogg

DOWNCA_BOOTANIMATION_FILE=/data/shared/cust/bootanimation.zip
DOWNCA_BOOTANIMATION_SOUND_FILE=/data/shared/cust/PowerOn.ogg
DOWNCA_SHUTDOWNANIMATION_FILE=/data/shared/cust/shutdownanimation.zip
DOWNCA_SHUTDOWNANIMATION_SOUND_FILE=/data/shared/cust/PowerOff.ogg


rm $USER_BOOTANIMATION_FILE
rm $USER_BOOTANIMATION_SOUND_FILE
rm $USER_SHUTDOWNANIMATION_FILE
rm $USER_SHUTDOWNANIMATION_SOUND_FILE

if [ -f $DOWNCA_BOOTANIMATION_FILE ]; then
    ln -s $DOWNCA_BOOTANIMATION_FILE $USER_BOOTANIMATION_FILE
fi

if [ -f $DOWNCA_BOOTANIMATION_SOUND_FILE ]; then
    ln -s $DOWNCA_BOOTANIMATION_SOUND_FILE $USER_BOOTANIMATION_SOUND_FILE
fi

if [ -f $DOWNCA_SHUTDOWNANIMATION_FILE ]; then
    ln -s $DOWNCA_SHUTDOWNANIMATION_FILE $USER_SHUTDOWNANIMATION_FILE
fi

if [ -f $DOWNCA_SHUTDOWNANIMATION_SOUND_FILE ]; then
    ln -s $DOWNCA_SHUTDOWNANIMATION_SOUND_FILE $USER_SHUTDOWNANIMATION_SOUND_FILE
fi

exit 0